import React, { useState, useEffect } from 'react'
import Skeleton from 'react-loading-skeleton';
import { Link } from 'react-router-dom';

export default function Product() {
    const [data, setData] = useState([])
    const [filter, setFilter] = useState(data)
    const [loading, setLoading] = useState(false)

    let cpmponentMounted = true;

    useEffect(() => {
        const getProduct = async () => {

            setLoading(true);
            const response = await fetch("https://fakestoreapi.com/products");
            if (cpmponentMounted) {
                setData(await response.clone().json());
                setFilter(await response.json());
                setLoading(false);
                console.log(filter)

            }
            return () => {
                cpmponentMounted = false;
            }
        }
        getProduct();

    }, [])

    const Loading = () => {
        return (
            <>
                <div className='col-md-3'>
                    <Skeleton height ={350}/>
                </div>
                <div className='col-md-3'>
                    <Skeleton height ={350}/>
                </div>
                <div className='col-md-3'>
                    <Skeleton height ={350}/>
                </div>
                <div className='col-md-3'>
                    <Skeleton height ={350}/>
                </div>

            </>
        )
    }
    const filterProduct = (cat) => {
        const updatedList =data.filter((x)=>x.category === cat);
         setFilter(updatedList);
    }
    const ShowProduct = () => {
        return (
            <>
                <div className='buttons d-flex justify-content-center mb-5 py-5'>
                    <button className='btn btn-outline-dark me-2' onClick={()=>setFilter(data)}>All</button>
                    <button className='btn btn-outline-dark me-2'onClick={()=>filterProduct("men's clothing")}>Men's clothing</button>
                    <button className='btn btn-outline-dark me-2'onClick={()=>filterProduct("Women's clothing")}>Women's clothing</button>
                    <button className='btn btn-outline-dark me-2'onClick={()=>filterProduct("jewelery")}>jewelery</button>
                    <button className='btn btn-outline-dark me-2'onClick={()=>filterProduct("Electronic")}>Electronic</button>
                    {/* <button className='btn btn-outline-dark'>All</button> */}
                </div>
                {filter.map((product) => {
                    return (
                        <>
                            <div className='col-md-3 mb-4'>
                                <div class="card h-100 text-center p-4 " key={product.id}>
                                    <img src={product.image} class="card-img-top" alt={product.title} height = {'250px'}/>
                                        <div class="card-body">
                                            <h5 class = "card-title mb-0">{product.title.substring(0,12)}...</h5>
                                            <p class="card-text lead fw-bold ">${product.price}</p>
                                            <Link to={`/products/${product.id} `} className='btn btn-outline-dark'>
                                                Buy Now 
                                            </Link>
                                        </div>
                                </div>
                            </div>
                        </>
                    )
                })}
            </>
        )
    }

    return (
        <div>
            <div className='container my-5 py-5'>
                <div className='row'>
                    <div className='col-12 mb-5'>
                        <h1 className='display-6 fw-bolder text-center'>Products</h1>
                        <hr />
                    </div>
                </div>
                <div className='row justify-content-center'>
                    {loading ? <Loading /> : <ShowProduct />}
                </div>
            </div>
        </div>
    )
}
